/* human message */
.Message_humanMessageBubble__Nld4j {
    max-width: 70vw !important;
    justify-content: flex-start !important;
    margin-left: 0 !important;
    background: white !important;
    color: black !important;

    border-left: 2px solid #4484e3 !important;
    border-radius: 0px !important;
}

.Message_humanMessageBubble__Nld4j::before {
    content: "";
    position: absolute;
    top: 0;
    left: -10px;
    /* Adjust the left value to change the position of the line */
    bottom: 0;
    width: 3px;
    /* Adjust the width value to change the thickness of the line */
    background-color: #0077cc;
    /* Change the color of the line */
}


